import {Component, EventEmitter, Input, Output, TemplateRef, ViewChild, ViewContainerRef} from '@angular/core';
import {CdkDragDrop, moveItemInArray} from '@angular/cdk/drag-drop';

import {TemplatePortal} from '@angular/cdk/portal';
import {fromEvent, Subscription} from 'rxjs';
import {filter, take} from 'rxjs/operators';
import {Overlay, OverlayRef} from '@angular/cdk/overlay';

@Component({
  selector: 'pm-draggable-list',
  templateUrl: './draggable-list.component.html',
  styleUrls: ['./draggable-list.component.scss']
})
export class DraggableListComponent {

  @Input('enableDragging') enableDragging = true;
  @Input('enableSelection') enableSelection = true;
  @Output() onSelectedChange = new EventEmitter();
  private selectedItem: any;

  @Input('showIndex') showIndex = true;
  @Input('itemList') itemList: any[];
  @Input('itemActionLabel') itemActionLabel;
  @Input('itemMainLabel') itemMainLabel;
  @Input('itemSubLabel') itemSubLabel;
  @Input('actions') actions: any[];

  @Input('roundedBorders') roundedBorders = true;

  sub: Subscription;
  overlayRef: OverlayRef | null;
  @ViewChild('contextMenu') contextMenu: TemplateRef<any>;

  constructor(
    public overlay: Overlay,
    public viewContainerRef: ViewContainerRef,
  ) {
  }

  public getItemList(): any[] {
    return this.itemList;
  }

  public setItemList(itemList: any[]): void {
    this.itemList = itemList;
  }

  public getSelectedItem(): any {
    return this.selectedItem;
  }

  /**
   * Setta o novo item como selecionado
   * @param item: item novo
   */
  public setSelectedItem(item: any): void {
    if (this.enableSelection === true) {
      this.selectedItem = item;
      this.onSelectedChange.emit(item);
    }
  }

  /**
   * Evento de drop do cdkDragDrop
   * @param event: evento de drop
   * @param profileList: lista a ser dropado
   */
  public drop(event: CdkDragDrop<string[]>): void {
    if (this.enableDragging) {
      moveItemInArray(this.itemList, event.previousIndex, event.currentIndex);
    }
  }

  /**
   * Abre o context Menu
   * @param x: posição x do mouse
   * @param y: posição y do mouse
   * @param context: valores que serão passados para o context menu
   */
  public openContextMenu({x, y}: MouseEvent, context): void {
    this.closeContextMenu();
    const positionStrategy = this.overlay.position()
      .flexibleConnectedTo({x, y})
      .withPositions([
        {
          originX: 'end',
          originY: 'bottom',
          overlayX: 'end',
          overlayY: 'top',
        }
      ]);

    this.overlayRef = this.overlay.create({
      positionStrategy,
      scrollStrategy: this.overlay.scrollStrategies.close()
    });

    this.overlayRef.attach(new TemplatePortal(this.contextMenu, this.viewContainerRef, context));

    this.sub = fromEvent<MouseEvent>(document, 'click')
      .pipe(
        filter(event => {
          const clickTarget = event.target as HTMLElement;
          return !!this.overlayRef && !this.overlayRef.overlayElement.contains(clickTarget);
        }),
        take(1)
      ).subscribe(() => this.closeContextMenu());
  }

  /**
   * Fecha o context menu
   */
  public closeContextMenu(): void {
    // tslint:disable-next-line:no-unused-expression
    this.sub && this.sub.unsubscribe();
    if (this.overlayRef) {
      this.overlayRef.dispose();
      this.overlayRef = null;
    }
  }

  /**
   * Caso o originalString tenha {algumacoisa}, é substituído por ${item[algumacoisa]}
   * Fazendo assim ser possível strings dinânimcas de acordo com o objeto
   * Caso o item seja string, é possível ele ser dinâmico usando {this}
   */
  public formatItemStringParameters(originalString: string, item: any): string {
    if (typeof item !== 'string' && originalString.indexOf('{') !== -1) {
      let result = '';
      for (const str of originalString.split('{')) {
        if (str.indexOf('}') !== -1) {
          result += `${item[str.substring(0, str.indexOf('}'))]}`;
          result += str.substring(str.indexOf('}') + 1, str.length);
        } else {
          result += str;
        }
      }
      return result;
    } else {
      return originalString.replace('{this}', item);
    }
  }

  /**
   * Verifica se o item é o atual selecionado
   * @param item: item a ser comparado com o atual selecionado
   */
  public isSelectedItem(item: any): boolean {
    if (typeof item !== 'undefined') {
      return this.selectedItem === item;
    } else {
      return false;
    }
  }


}

import {
  AfterViewInit,
  ChangeDetectorRef,
  Component,
  EventEmitter,
  Input,
  Output,
  TemplateRef,
  ViewChild
} from '@angular/core';
import {Sizes} from '../../models/sizes';
import {animate, state, style, transition, trigger} from '@angular/animations';
import {MatTable, MatTableDataSource} from '@angular/material/table';

@Component({
  selector: 'pm-table',
  templateUrl: './table.component.html',
  styleUrls: ['./table.component.scss'],
  animations: [
    trigger('detailExpand', [
      state('collapsed', style({height: '0px', minHeight: '10px', visibility: 'visible'})),
      state('expanded', style({height: '50px', visibility: 'visible'})),
      transition('expanded <=> collapsed', animate('225ms cubic-bezier(0.4, 0.0, 0.2, 1)')),
    ]),
  ],
})
export class TableComponent implements AfterViewInit {

  @Output() onItemListChange = new EventEmitter();
  @Input() columns: string[] = [];
  @Input() columnNames: string[] = [];
  @Input() imageColumns: string[] = [];
  @Input() imageHeight: '50px';
  @Input() showHeader = true;
  @Input() numberFormat = '1.2-2';
  @Input() numberPrefix = '';
  @Input() actionsTemplate: TemplateRef<any>;
  @Input() itemList: any = [];
  @Input() borderRadius: Sizes;
  @Input() backgroundColor: string;

  @ViewChild('table', {static: true}) table: MatTable<any>;
  @ViewChild('singleLabelCell', {static: true}) singleLabelCell: TemplateRef<any>;
  @ViewChild('doubleLabelCell', {static: true}) doubleLabelCell: TemplateRef<any>;
  @ViewChild('imageCell', {static: true}) imageCell: TemplateRef<any>;
  @ViewChild('formattedNumberCell', {static: true}) formattedNumberCell: TemplateRef<any>;

  public expandedRow: any;
  // tslint:disable-next-line:ban-types
  isExpansionDetailRow = (i: number, row: Object) => row.hasOwnProperty('detailRow');

  constructor(
    private changeDetectorRef: ChangeDetectorRef
  ) {
  }

  ngAfterViewInit(): void {
    if (typeof this.actionsTemplate !== 'undefined') {
      this.columns.push('ACTIONS');
      this.columnNames.push('');
    }
    this.reloadExpandedDetail();
  }

  public setSelectedRow(row: any): void {
    console.log(row);
    this.expandedRow = row;
  }

  /**
   * Reconstroi os elementos da tabela para incluir o "detail row" e deixar ela expansível
   * @private
   */
  private reloadExpandedDetail(): void {
    const newItems = [];
    this.itemList.forEach(element => newItems.push(element, { detailRow: true, element }));
    this.itemList = new MatTableDataSource(newItems);
    this.changeDetectorRef.detectChanges();
  }

  /**
   * Retorna a lista atual de itens na tabela
   */
  public getItemList(): any[] {
    return this.itemList;
  }

  /**
   * Troca a lista de itens da tabela
   */
  public setItemList(itemList: any[]): void {
    this.itemList = itemList;
    this.reloadExpandedDetail();
    this.onItemListChange.emit(this.itemList);
  }

  /**
   * Mostra a linha de header
   */
  public enableHeader(): void {
    this.showHeader = true;
  }

  /**
   * Some com a linha de header
   */
  public disableHeader(): void {
    this.showHeader = false;
  }

  /**
   * Retorna o template que será renderizada a celula de acordo com o input de item e coluna
   * @param item: item da lista de itens
   * @param column: propriedade do item
   */
  public getCellTemplate(item: any, column: string): TemplateRef<any> {
    if (column === 'ACTIONS') {
      return this.actionsTemplate;
    } else if (Array.isArray(item[column])) {
      return this.doubleLabelCell;
    } else {
      if (this.imageColumns.includes(column)) {
        return this.imageCell;
      } else {
        if (!isNaN(item[column])) {
          return this.formattedNumberCell;
        } else {
          return this.singleLabelCell;
        }
      }
    }
  }
}

