import {CommonModule} from '@angular/common';
import {ModuleWithProviders, NgModule} from '@angular/core';
import {ColorThemeService} from './color-theme.service';
import {colorThemeToken, defaultColorTheme} from './injection';

export interface ColorThemeModuleConfig {
  theme?: string;
}

@NgModule({
  declarations: [],
  imports: [CommonModule],
  exports: [],
})
export class ColorThemeModule {
  public static forRoot(options: ColorThemeModuleConfig = {}): ModuleWithProviders<ColorThemeModule> {
    return {
      ngModule: ColorThemeModule,
      providers: [
        ColorThemeService,
        {provide: colorThemeToken, useValue: options.theme || defaultColorTheme},
      ]
    };
  }
}

import {ChangeDetectorRef, Component, EventEmitter, Input, OnInit, Output} from '@angular/core';

export interface ComboItem {
  label: string;
  value: any;
}

@Component({
  selector: 'pm-combo-box',
  templateUrl: './combo-box.component.html',
  styleUrls: ['./combo-box.component.scss']
})
export class ComboBoxComponent implements OnInit {

  /**
   * Itens que serão exibidos na combo box.
   * Devem ser obrigatoriamente do tipo `ComboItem[]`
   */
  @Input() items: ComboItem[] = [];
  /**
   * Item selecionado da combobox. Pode ser carregado previamente
   */
  @Input() selectedItem: any;
  /**
   * Desabilita a interação com os valores da combo box.
   */
  @Input() disabled: boolean = false;
  /**
   * Emite um valor booleano com o novo valor do item selecionado na combo box.
   */
  @Output() selectionChange: EventEmitter<any> = new EventEmitter<any>();

  constructor(private ref: ChangeDetectorRef) {
  }

  ngOnInit(): void {
    this.ref.detectChanges();
  }

  public setSelected(item: any): void {
    this.selectedItem = item;
    this.selectionChange.emit(item);
  }

  private selectionChanged(event: any): void {
    this.selectionChange.emit(event.value);
    this.ref.detectChanges();
  }

}

import {InjectionToken} from '@angular/core';
import {PaletteEntry} from './models';

export interface CommandPaletteConfig {
