import {Inject, Injectable} from '@angular/core';
import {colorThemeToken} from './injection';

export const defaultTheme = {
  'color-primary': '#455363',
  'color-default': '#455363',
  'background-color': '#1f2935',
  'text-color': '#fff'
};

export const lightTheme = {
  'color-primary': '#fff',
  'color-default': '#fff',
  'background-color': '#e5e5e5',
  'text-color': '#2d2d2d'
};

export const darkTheme = {
  'color-primary': '#455363',
  'color-default': '#455363',
  'background-color': '#1f2935',
  'text-color': '#fff'
};

@Injectable()
export class ColorThemeService {

  private _theme: string;

  constructor(
    @Inject(colorThemeToken) initialTheme: any
  ) {
    this.theme = initialTheme;
  }

  public get theme(): string {
    return this._theme;
  }

  public set theme(theme: string) {
    this._theme = theme;

    let themeObj;
    switch (theme) {
      case 'default':
        themeObj = defaultTheme;
        break;
      case 'light':
        themeObj = lightTheme;
        break;
      case 'dark':
        themeObj = darkTheme;
        break;
      default:
        themeObj = defaultTheme;
        break;
    }
    Object.keys(themeObj).forEach(key =>
      document.documentElement.style.setProperty(`--${key}`, `${themeObj[key]}`, 'important')
    );
  }

  public toggle(): void {
    this.theme = this._theme === 'dark' ? 'light' : 'dark';
  }
}

import { Injectable } from '@angular/core';
import {PaletteEntry} from './models';
import {BehaviorSubject, Observable} from 'rxjs';

@Injectable()
export class CommandPaletteEntriesService {

  public dialogSearchPlaceholder = '...';
  public dialogClearButtonLabel = 'CLEAR';

  private _paletteEntries: PaletteEntry[] = [];
  private paletteEntries$$: BehaviorSubject<PaletteEntry[]> = new BehaviorSubject<PaletteEntry[]>(this._paletteEntries);
  public readonly paletteEntries$: Observable<PaletteEntry[]> = this.paletteEntries$$.asObservable();

  constructor() { }

  public get paletteEntries(): PaletteEntry[] {
    return this._paletteEntries;
  }

  public set paletteEntries(paletteEntries: PaletteEntry[]) {
    this._paletteEntries = paletteEntries;
    this.paletteEntries$$.next(this._paletteEntries);
  }
}

import {
  Inject,
  Injectable,
} from '@angular/core';
import {COMMAND_PALETTE_CONFIG, CommandPaletteConfig} from './injection';
import {DialogComponent} from './dialog/dialog.component';
import {ComponentInjectorService} from './component-injector.service';
import {Hotkey, HotkeysService} from 'angular2-hotkeys';
import {CommandPaletteEntriesService} from './command-palette-entries.service';
import {PaletteEntry} from './models';

@Injectable()
export class CommandPaletteService {
  private _configs: CommandPaletteConfig;

  constructor(
    @Inject(COMMAND_PALETTE_CONFIG) initialConfig: CommandPaletteConfig,
    public commandPaletteEntriesService: CommandPaletteEntriesService,
    private componentInjectorService: ComponentInjectorService,
    private hotkeysService: HotkeysService,
  ) {
    this._configs = initialConfig;
    this.commandPaletteEntriesService.paletteEntries = this._configs.paletteEntries;
    this.hotkeysService.add(new Hotkey(this._configs.hotkeys, (event: KeyboardEvent, combo: string): boolean => {
      event.stopPropagation();
      event.preventDefault();
      this.triggerDialog();
      return true;
    }));
  }

  public get configs(): CommandPaletteConfig {
    return this._configs;
  }

  public triggerDialog(initialEntry: PaletteEntry = undefined): void {
    this.componentInjectorService.appendComponentToBody(DialogComponent).then(
      (createdComponent) => {
        createdComponent.instance.itself = createdComponent;
        if (!!initialEntry) {
          setTimeout(() => {
            createdComponent.instance.initFromPaletteEntry(initialEntry);
          }, 0);
        }
      }
    );
  }
}

import {
  ApplicationRef,
  ComponentFactoryResolver,
  ComponentRef,
  EmbeddedViewRef,
  Injectable,
  Injector
} from '@angular/core';

@Injectable()
export class ComponentInjectorService {

  constructor(
    private componentFactoryResolver: ComponentFactoryResolver,
    private applicationRef: ApplicationRef,
    private injector: Injector
  ) { }

  async appendComponentToBody(component: any): Promise<ComponentRef<any>> {
    return await new Promise((resolve) => {
      // create a component reference
      const componentRef = this.componentFactoryResolver
        .resolveComponentFactory(component)
        .create(this.injector);

      // attach component to the appRef so that so that it will be dirty checked.
      this.applicationRef.attachView(componentRef.hostView);

      // get DOM element from component
      const domElem = (componentRef.hostView as EmbeddedViewRef<any>)
        .rootNodes[0] as HTMLElement;
      document.body.appendChild(domElem);
      resolve(componentRef);
    });
  }

  removeComponentFromBody(componentRef: ComponentRef<any>): void {
    this.applicationRef.detachView(componentRef.hostView);
    componentRef.destroy();
  }
}

