      border-bottom: none;
    }

    .mat-tab-header-pagination {
      display: none !important;
    }

    .mat-ink-bar {
      height: 100%;
      left: 100% !important;
      background-color: #51cbce33 !important;
    }

    .mat-tab-body-wrapper {
      flex: 1 1 auto;
      padding: 0px;
      overflow-y: auto;
      overflow-x: hidden;

