        height: 20px;
        width: 20px;
        border-radius: 50%;
        padding: 0px;
      }

      .bool-div-container {
        margin-top: 12px;
        margin-bottom: 5px;
      }
    }
  }

  .vertical-tabs {
    @extend .horizontal-tabs;

    .mat-tab-label {
      margin-top: 5px;
    }

    .mat-tab-group {
      flex-direction: row;
    }

    .mat-tab-labels {
      flex-direction: column;
    }

    .mat-tab-body-wrapper {
      padding-left: 10px;
    }

    .mat-tab-label-container {
      overflow-y: auto;
    }

    mat-tab-body {
      min-height: 100px;
    }
  }
}

